import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

interface Props {

}

export default function FileUpload(props : Props) {
    const onFormClick = () => {
        document.getElementById("fileUploadInput")?.click();
    }

    return <div className="w-full h-full flex flex-col justify-center group cursor-pointer" onClick={onFormClick}>
        <form className="hidden">
            <input type="file" id="fileUploadInput"></input>
        </form>
        <FontAwesomeIcon icon="file-upload" className="mx-auto text-white opacity-50 text-4xl group-hover:opacity-80 transition-opacity ease-in-out" />
    </div>;
}
