import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";
import prettyBytes from "pretty-bytes";

interface Props {
    handleFileUpload : (file : File | null) => void;
}

export default function FileUpload(props : Props) {
    const [curFile, setCurFile] = useState<File | null>(null);

    const onFormClick = () => {
        document.getElementById("fileUploadInput")?.click();
    }

    return <div className="w-full h-full flex flex-col justify-center group cursor-pointer" onClick={onFormClick} 
    onDragEnter={e => {
        e.preventDefault();
        e.stopPropagation();
    }}

    onDragLeave={e => {
        e.preventDefault();
        e.stopPropagation();
    }}

    onDragOver={e => {
        e.preventDefault();
        e.stopPropagation();
    }}

    onDrop={e => {
        e.preventDefault();
        e.stopPropagation();
    }}>
        <form className="hidden">
            <input type="file" id="fileUploadInput" onChange={(event) => {
                if(event.target.files != null && event.target.files.length > 0) {
                    props.handleFileUpload(event.target.files[0]);
                    setCurFile(event.target.files[0]);
                } else {
                    props.handleFileUpload(null);
                    setCurFile(null);
                }
            }}></input>
        </form>
        <FontAwesomeIcon icon={curFile == null ? "file-upload" : "archive"} className="mx-auto text-white opacity-50 text-4xl group-hover:opacity-80 transition-opacity ease-in-out" />
        {
            curFile == null ? null : <p className="mx-auto mt-4 text-white opacity-50 text-xl group-hover:opacity-80 transition-opacity ease-in-out">
                {curFile.name} | {prettyBytes(curFile.size)}
            </p>
        }
    </div>;
}
